REM INSERTING into 系统_树定义
SET DEFINE OFF@&;
decode((SELECT "菜单操作ID" FROM "系统_角色权限" WHERE "角色ID"=''$GroupID$'' AND "菜单ID"= A.ID AND ROWNUM = ''1'' AND "菜单操作ID" IS NOT NULL),Null,0,1)AS "Check"
FROM "系统_菜单" A WHERE "模块代码"=''$ModuleCode$'' order by "排序序号" asc',null,null,0,1,'菜单权限维护',0,'菜单权限维护',null,null,'李兴贵',null,4,0,1,null)@&;
              b.操作代码 AS "Name",
              b.菜单ID AS "MenuID",
              b.是否控制权限 AS "IsCtl",
              CASE
                  WHEN c.菜单操作ID IS NULL THEN 0
                  ELSE 1
               END
              AS "Check"
       FROM 系统_菜单 a,系统_菜单操作 b
       left join 系统_角色权限 c on c.菜单操作ID=b.ID  and c.角色ID=''$GroupID$''
       where a.ID = b.菜单ID and b.是否控制权限=1 and a.ID = ''$ID$''
       union all
       SELECT b.ID,
              b.操作代码 AS "Name",
              b.菜单ID AS "MenuID",
              b.是否控制权限 AS "IsCtl",
              1 AS "Check"
       FROM 系统_菜单 a,系统_菜单操作 b
       where a.ID = b.菜单ID and b.是否控制权限=0 and a.ID = ''$ID$''',null,null,0,0,null,0,null,null,null,null,null,0,0,1,null)@&;
       a.名称 AS "Name",
       a.上级ID AS "SuperID",
       a.层级 AS "Grade",
       a.是否末级 AS "IsLast",
       a.打开方式 AS "OpenType",
       e.模块代码 AS "ModuleCode",
       a.排序序号 As "SeqNo",
       CASE
          WHEN f.地址 IS NOT NULL THEN f.地址 || ''&iexcl;'' || f.默认参数
          ELSE NULL
       END
          AS "URL"
  FROM 系统_菜单 a
       INNER JOIN 系统_角色权限 b ON a.ID = b.菜单ID
       INNER JOIN 系统_角色成员 c ON b.角色ID = c.角色ID
       INNER JOIN 系统_用户信息 d ON c.用户ID = d.职工相关ID
       INNER JOIN 系统_模块 e ON e.模块代码 = a.模块代码
       LEFT JOIN 系统_功能 f ON a.功能ID = f.ID
 WHERE     a.是否作废 = 0
       AND a.是否启用权限 = 1
       AND b.菜单操作ID = ''-1''
       AND d.ID = ''$UserID$''
       AND e.模块代码 = ''$ModuleCode$''
      
UNION ALL
SELECT a.ID,
       a.名称 AS "Name",
       a.上级ID AS "SuperID",
       a.层级 AS "Grade",
       a.是否末级 AS "IsLast",
       a.打开方式 AS "OpenType",
       b.模块代码 AS "ModuleCode",
       a.排序序号 As "SeqNo",
       CASE
          WHEN c.地址 IS NOT NULL THEN c.地址 || ''&iexcl;'' || c.默认参数
          ELSE NULL
       END
          AS "URL"
  FROM 系统_菜单 a
       INNER JOIN 系统_模块 b ON b.模块代码 = a.模块代码
       LEFT JOIN 系统_功能 c ON a.功能ID = c.ID
 WHERE     a.是否作废 = 0
       AND a.是否启用权限 = 0
       AND b.模块代码 = ''$ModuleCode$'')
 Order By "SeqNo" asc',null,null,0,1,'主菜单',1,'主菜单',null,null,'李兴贵',null,2,0,1,null)@&;
